DROP TABLE IF EXISTS carreras;
CREATE TABLE carreras (
  `id` BIGINT NOT NULL AUTO_INCREMENT ,
  `area` BIGINT NULL  ,
  `clave_carrera` VARCHAR(255) NULL  ,
  `clave_plantel` VARCHAR(255) NULL  ,
  `carrera` VARCHAR(255) NULL  ,
  `plantel` VARCHAR(255) NULL  ,
 PRIMARY KEY (id) 
);



DROP TABLE IF EXISTS codigos;
CREATE TABLE codigos (
  `internet` INT NULL  ,
  `code` VARCHAR(255) NOT NULL  ,
  `description` VARCHAR(255) NULL  ,
  `color` VARCHAR(255) NULL  ,
  `color_rgb` VARCHAR(255) NULL  ,
  `orden` VARCHAR(255) NULL  ,
);



DROP TABLE IF EXISTS reactivos;
CREATE TABLE reactivos (
  `id` BIGINT NOT NULL AUTO_INCREMENT ,
  `orden` DOUBLE NULL  ,
  `child` BIGINT NULL  ,
  `aux` BIGINT NULL  ,
  `created_at` DATE NULL  ,
  `updated_at` DATE NULL  ,
  `act_order` DOUBLE NULL  ,
  `archtype` VARCHAR(255) NULL  ,
  `clave` VARCHAR(255) NULL  ,
  `description` VARCHAR(255) NULL  ,
  `type` VARCHAR(255) NULL  ,
  `rules` TEXT NULL  ,
  `section` VARCHAR(255) NULL  ,
  `extra_label` VARCHAR(255) NULL  ,
 PRIMARY KEY (id) 
);



DROP TABLE IF EXISTS bloqueos;
CREATE TABLE bloqueos (
  `id` BIGINT NOT NULL AUTO_INCREMENT ,
  `valor` BIGINT NULL  ,
  `clave_reactivo` VARCHAR(255) NULL  ,
  `bloqueado` VARCHAR(255) NULL  ,
 PRIMARY KEY (id) 
);



DROP TABLE IF EXISTS options;
CREATE TABLE options (
  `updated_at` DATE NULL  ,
  `clave` BIGINT NULL  ,
  `id` BIGINT NOT NULL AUTO_INCREMENT ,
  `help_info` VARCHAR(255) NULL  ,
  `descripcion` VARCHAR(255) NULL  ,
  `reactivo` VARCHAR(255) NULL  ,
  `orden` VARCHAR(255) NULL  ,
 PRIMARY KEY (id) 
);



DROP TABLE IF EXISTS muestras;
CREATE TABLE muestras (
  `poblacion` BIGINT NULL  ,
  `estudio_id` BIGINT NULL  ,
  `dispersion` DOUBLE NULL  ,
  `id` BIGINT NOT NULL AUTO_INCREMENT ,
  `enc_id` BIGINT NULL  ,
  `requeridas_10` BIGINT NULL  ,
  `requeridas_5` BIGINT NULL  ,
  `carrera_id` VARCHAR(255) NULL  ,
  `plantel_id` VARCHAR(255) NULL  ,
 PRIMARY KEY (id) 
);



DROP TABLE IF EXISTS estudios;
CREATE TABLE estudios (
  `id` BIGINT NOT NULL AUTO_INCREMENT ,
  `gen` BIGINT NULL  ,
  `actualizacion` BIGINT NULL  ,
  `start_date` DATE NULL  ,
  `end_date` VARCHAR(255) NULL  ,
  `estudiocol` VARCHAR(255) NULL  ,
 PRIMARY KEY (id) 
);



