DROP TABLE IF EXISTS comentario;
CREATE TABLE comentario (
  `registro` BIGINT NOT NULL AUTO_INCREMENT ,
  `created_at` DATE NULL  ,
  `updated_at` DATE NULL  ,
  `cuenta` VARCHAR(255) NULL  ,
  `comentario` TEXT NULL  ,
 PRIMARY KEY (registro) 
);


